import * as loginApi from "../../apis/login";
import * as loginConst from "../constrants/loginConst";
import Cookies from "js-cookie";
import jwt from "jwt-decode";
import cookie from "react-cookie";

export const postLogin = () => {
  return {
    type: loginConst.POST_LOGIN,
  };
};

export const postLoginSuccess = (data) => {
  return {
    type: loginConst.POST_LOGIN_SUCCESS,
    payload: {
      data: data,
    },
  };
};

export const postLoginFailed = (error) => {
  return {
    type: loginConst.POST_LOGIN_FAILED,
    payload: {
      error,
    },
  };
};

export const postLoginReport = (req) => {
  return async (dispatch) => {
    await loginApi
      .login(
        {
          email: "19521679@gm.uit.edu.vn",
          password: "1",
        },
        {
          // credentials: 'include',
          withCredentials: true,
          // headers: {
          //   Accept: "/",
          //   "Cache-Control": "no-cache",
          //   "Set-Cookie": "token=eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1bmlxdWVfbmFtZSI6IjE5NTIxNjc5QGdtLnVpdC5lZHUudm4iLCJyb2xlIjoiYWRtaW5pc3RyYXRvciIsIm5iZiI6MTYzODk0OTYyNSwiZXhwIjoxNjM4OTUzMjI1LCJpYXQiOjE2Mzg5NDk2MjV9.K3T4i2xVTIGVC2Q-32PGDsUtYmwl8DIp97Hk4gZKjB0",
          // },
        }
      )
      .then((success) => {
        if (success.status === 200) {
          dispatch(postLoginSuccess(success.data));
          // const token = success.data;
          // const user = jwt(token); // decode your token here
          console.log(success);
        } else dispatch(postLoginFailed(success));
      })
      .catch((error) => {
        dispatch(postLoginFailed(error));
      });

  };
};
