import React, { Component } from "react";

export default class Item extends Component {
  render() {
    return (
      <tr>
        <td className="align-middle text-center">
          <span className="text-secondary text-xs font-weight-bold">
            {this.props.khachhang.tenkhachhang}
          </span>
        </td>
        <td className="align-middle text-center">
          <span className="text-secondary text-xs font-weight-bold">
          {this.props.khachhang.email}
          </span>
        </td>
        <td className="align-middle text-center">
          <span className="text-secondary text-xs font-weight-bold">
          {this.props.khachhang.sodienthoai}
          </span>
        </td>
        <td className="align-middle text-center">
          <span className="text-secondary text-xs font-weight-bold">
          {this.props.khachhang.diachi}
          </span>
        </td>
        <td className="align-middle text-center">
          <span className="text-secondary text-xs font-weight-bold">
          {this.props.khachhang.ngaysinh}
          </span>
        </td>
        {this.props.khachhang.loaikhachhang === "Thành viên" ? (
          <td className="align-middle text-center text-sm">
            <span className="badge badge-sm bg-gradient-secondary">
            Thành viên
            </span>
          </td>
        ) : (
          <td className="align-middle text-center text-sm">
            <span className="badge badge-sm bg-gradient-success">Thường</span>
          </td>
        )}
        <td className="align-middle">
          <div
            className="btn btn-link text-dark px-3 mb-0"
            onClick={() => this.setState({ showModal: 1 })}
          >
            <i class="bi bi-pencil-square"></i>
            {"  "}Sửa
          </div>
          <div
            className="btn btn-link text-danger px-3 mb-0 " style={{ position: 'relative', zIndex: '0' }}
            onClick={() => this.setState({ showModal: 2 })}
          >
            <i class="bi bi-trash"></i>
            {"  "}Xoá
          </div>
        </td>
      </tr>
    );
  }
}
