import React, { useState, useEffect, useRef } from "react";
import "./style.css";
import * as productApi from "../apis/product";
import LoadingContainer from "../../Common/helper/loading/LoadingContainer";

export default function Index(props) {
  const [timkiem, setTimkiem] = useState("");
  const [showSearch, setShowSearch] = useState(false);
  const [list, setList]= useState([]);
  const ref = useRef(null);
  const [loading, setLoading]= useState(false);
  useEffect(() => {
    /**
     * Alert if clicked on outside of element
     */
    function handleClickOutside(event) {
      if (ref.current && !ref.current.contains(event.target)) {
        setShowSearch(false);
      }
    }

    // Bind the event listener
    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      // Unbind the event listener on clean up
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, [ref]);

  const hanldeSearch= async (timkiem)=>{
      setLoading(true);
    await productApi.timkiem6Sanpham(timkiem)
    .then(success => {
        if (success.status===200) {
            setList(success.data.value.$value);
        }
    })
    .catch(error => {
        console.error(error);
    });
    setLoading(false);
  }
  return (
    <form
      id="form-box-search"
      className="box-main__form-search position-relative"
      action="https://cellphones.com.vn/catalogsearch/result/"
      method="get"
      autoComplete="off"
      ref={ref}
    >
        <LoadingContainer loading={loading} ></LoadingContainer>
      <div className="box-search">
        <div className="cps-group-input">
          <div className="input-group-btn">
            <i class="bi bi-search"></i>
          </div>
          <input
            type="text"
            name="q"
            id="search"
            className="cps-input cta-search"
            placeholder="Bạn cần tìm gì?"
            maxLength={128}
            autoComplete="off"
            value={timkiem}
            onChange={(e) => 
               {
                   setTimkiem(e.target.value) 
                   if (e.target.value===""){
                    setShowSearch(false); 
                   }
                   else{
                    setShowSearch(true); 
                    hanldeSearch(e.target.value);
                   }
            }
            }
          />
          <div className="close-group-btn">
            <i
              class="fas fa-times"
              onClick={()=>{setShowSearch(false); setTimkiem("") }}
            ></i>
          </div>
        </div>
      </div>

      {showSearch && (
        <div
          id="search_autocomplete"
          className="box-search-result search-autocomplete box-shadow"
          // style={{ display: "none" }}
        >
          <ul className="search_suggest_data">
            <li style={{ display: "block" }} className />
            <li className="odd first last">
              <a href="https://cellphones.com.vn/iphone-13.html">
                <div className="box-img">
                  <img src="https://cdn.cellphones.com.vn/media/catalog/product/i/p/ip13-pro_2.jpg" />
                </div>
                <div className="box-info">
                  <div className="box-name">
                    <p>iPhone 13 | Chính hãng VN/A</p>
                  </div>
                  <div className="box-price">
                    <p className="special-price">24.990.000&nbsp;₫</p>
                  </div>
                </div>
              </a>
            </li>
            <li className="even first last">
              <a href="https://cellphones.com.vn/iphone-13-mini.html">
                <div className="box-img">
                  <img src="https://cdn.cellphones.com.vn/media/catalog/product/i/p/iphone-13-01_6.jpg" />
                </div>
                <div className="box-info">
                  <div className="box-name">
                    <p>iPhone 13 mini | Chính hãng VN/A</p>
                  </div>
                  <div className="box-price">
                    <p className="special-price">20.900.000&nbsp;₫</p>
                    <p className="old-price">21.990.000&nbsp;₫</p>
                  </div>
                </div>
              </a>
            </li>
            <li className="odd first last">
              <a href="https://cellphones.com.vn/iphone-13-pro.html">
                <div className="box-img">
                  <img src="https://cdn.cellphones.com.vn/media/catalog/product/i/p/iphone_13-_pro-5.jpg" />
                </div>
                <div className="box-info">
                  <div className="box-name">
                    <p>iPhone 13 Pro | Chính hãng VN/A</p>
                  </div>
                  <div className="box-price">
                    <p className="special-price">31.990.000&nbsp;₫</p>
                  </div>
                </div>
              </a>
            </li>
            <li className="even first last selected">
              <a href="https://cellphones.com.vn/iphone-13-pro-max.html">
                <div className="box-img">
                  <img src="https://cdn.cellphones.com.vn/media/catalog/product/i/p/iphone_13-_pro-5_4.jpg" />
                </div>
                <div className="box-info">
                  <div className="box-name">
                    <p>iPhone 13 Pro Max | Chính hãng VN/A</p>
                  </div>
                  <div className="box-price">
                    <p className="special-price">34.990.000&nbsp;₫</p>
                  </div>
                </div>
              </a>
            </li>
            <li className="odd first last">
              <a href="https://cellphones.com.vn/iphone-13-512gb.html">
                <div className="box-img">
                  <img src="https://cdn.cellphones.com.vn/media/catalog/product/i/p/iphone-13-02_4.jpg" />
                </div>
                <div className="box-info">
                  <div className="box-name">
                    <p>iPhone 13 512GB | Chính hãng VN/A</p>
                  </div>
                  <div className="box-price">
                    <p className="special-price">28.500.000&nbsp;₫</p>
                    <p className="old-price">33.990.000&nbsp;₫</p>
                  </div>
                </div>
              </a>
            </li>
          </ul>
        </div>
      )}
    </form>
  );
}
