import * as loadingConst from "../constrants/loadingConst";

const initialState = {
  loading: false
};
const reducer = (state = initialState, action) => {
  switch (action.type) {
    case loadingConst.LOADING: {
      return { ...state, loading :true};
    }
    case loadingConst.SHOW_LOADING: {
      // myToast.toastSucces("Đăng nhập thành công");
      return {
        ...state,loading :true
      };
    }
    case loadingConst.HIDE_LOADING: {
      return { ...state, loading:false};
    }
    default:
      return state;
  }
};
export default reducer;
