import React, { Component } from "react";
import "reactjs-popup/dist/index.css";
import Modal from "react-modal";
import * as productApi from "../../apis/product";
import * as myToast from "../../../Common/helper/toastHelper";
import * as staffApi from "../../apis/staff";

const customStyles = {
  content: {
    top: "40%",
    left: "50%",
    right: "auto",
    bottom: "auto",
    marginRight: "-50%",
    transform: "translate(-50%, -50%)",
  },
};

export default class DeleteModal extends Component {
  constructor(props) {
    super(props);
    this.state = {};
  }

  submitHandler() {
    staffApi
      .deleteStaff(this.props.staff.manhanvien)
      .then((success) => {
        if (success.status) {
          myToast.toastSucces("Xoá nhân viên thành công");
          this.props.delete(this.props.staff);
          this.props.closeModal();
        }
      })
      .catch((error) => {
        myToast.toastError("Xoá nhân viên thất bại");
        console.error(error);
      });
  }

  render() {
    return (
      <Modal
        isOpen={this.props.showModal}
        onRequestClose={this.props.closeModal}
        style={customStyles}
        contentLabel="Example Modal"
      >
        <div class="add-item-modal" role="document">
          <div class="">
            <div class="modal-header">
              <h5 class="modal-title" id="exampleModalLongTitle">
                Bạn có muốn xoá nhân viên này
              </h5>
            </div>

            <div class="modal-footer">
              <button
                type="button"
                class="btn btn-secondary"
                data-dismiss="modal"  
                onClick={this.props.closeModal}
              >
                Đóng
              </button>
              <button
                type="button"
                class="btn btn-primary"
                onClick={this.submitHandler.bind(this)}
              >
                Xoá
              </button>
            </div>
          </div>
        </div>
      </Modal>
    );
  }
}
