import React, { Component } from "react";
import "reactjs-popup/dist/index.css";
import Modal from "react-modal";

const customStyles = {
  content: {
    top: "40%",
    left: "50%",
    right: "auto",
    bottom: "auto",
    marginRight: "-50%",
    transform: "translate(-50%, -50%)",
  },
};

export default class AddCustomerModal extends Component {
  constructor(props) {
    super(props);
    this.state = {
      tensanpham : "",
      maloai: undefined,
      mathuonghieu : undefined ,
      soluongton : undefined,
      mota : "",
      image: undefined,
      thoidiemramat : new Date(),
      dongia: undefined
    };
  }

  render() {
    return (
      <Modal
        isOpen={this.props.showModal}
        onRequestClose={this.props.closeModal}
        style={customStyles}
        contentLabel="Example Modal"
      >
        <div class="add-item-modal" role="document">
          <div class="">
            <div class="modal-header">
              <h5 class="modal-title" id="exampleModalLongTitle">
                Thêm mới sản phẩm
              </h5>
            </div>

            <div className="row mb-1">

        </div>

        <div className="row mb-1">
          <div className="col-xs-6 col-sm-6 col-md-6 col-lg-6">
            Tên sản phẩm
          </div>
          <div className="col-xs-6 col-sm-6 col-md-6 col-lg-6 ">
            <input
              className="form-control border "
              id="tensanpham"
              placeholder=""
              onChange={((e) => {
                this.setState({ tensanpham: e.target.value });
              })}
              value={this.state.tensanpham}
            ></input>
          </div>
        </div>

        <div className="row mb-1">
          <div className="col-xs-6 col-sm-6 col-md-6 col-lg-6">
            Mã loại
          </div>
          <div className="col-xs-6 col-sm-6 col-md-6 col-lg-6 ">
            <input
              className="form-control border"
              id="maloai"
              placeholder=""
              onChange={((e) => {
                this.setState({ maloai: e.target.value });
              })}
              value={this.state.maloai}
            ></input>
          </div>
        </div>

        <div className="row mb-1">
          <div className="col-xs-6 col-sm-6 col-md-6 col-lg-6">
            Mã thương hiệu
          </div>
          <div className="col-xs-6 col-sm-6 col-md-6 col-lg-6 ">
            <input
              className="form-control border"
              id="mathuonghieu"
              placeholder=""
              onChange={((e) => {
                this.setState({ mathuonghieu: e.target.value });
              })}
              value={this.state.mathuonghieu}
            ></input>
          </div>
        </div>

        <div className="row mb-1">
          <div className="col-xs-6 col-sm-6 col-md-6 col-lg-6">
            Số lượng tồn
          </div>
          <div className="col-xs-6 col-sm-6 col-md-6 col-lg-6 ">
            <input
              className="form-control border"
              id="soluongton"
              placeholder=""
              onChange={((e) => {
                this.setState({ soluongton: e.target.value });
              })}
              value={this.state.soluongton}
            ></input>
          </div>
        </div>

        <div className="row mb-1">
          <div className="col-xs-6 col-sm-6 col-md-6 col-lg-6">
            Image
          </div>
          <div className="col-xs-6 col-sm-6 col-md-6 col-lg-6 ">
            <input
              className="form-control border"
              id="soluongton"
              placeholder=""
              onChange={((e) => {
                this.setState({ soluongton: e.target.value });
              })}
              value={this.state.soluongton}
            ></input>
          </div>
        </div>

        <div className="row mb-1">
          <div className="col-xs-6 col-sm-6 col-md-6 col-lg-6">
            Thời điểm ra mắt
          </div>
          <div className="col-xs-6 col-sm-6 col-md-6 col-lg-6 ">
            <input
              className="form-control border"
              type="date"
              id="ngayhoadon"
              name="trip-start"
              onChange={((e) => {
                this.setState({ thoidiemramat: new Date(e.target.value) });
              })}
              value={(this.state.thoidiemramat).toISOString().split('T')[0]}
            ></input>
          </div>
        </div>
        <div className="row mb-1">
          <div className="col-xs-6 col-sm-6 col-md-6 col-lg-6">
            Đơn giá
          </div>
          <div className="col-xs-6 col-sm-6 col-md-6 col-lg-6 ">
            <input
              className="form-control border"
              id="dongia"
              placeholder=""
              onChange={((e) => {
                this.setState({ dongia: e.target.value });
              })}
              value={this.state.dongia}
            ></input>
          </div>
        </div>

        <hr></hr>


            <div class="modal-footer">
              <button
                type="button"
                class="btn btn-secondary"
                data-dismiss="modal"
                onClick={this.props.closeModal}
              >
                Đóng
              </button>
              <button
                type="button"
                class="btn btn-primary"
                onClick={()=>{this.props.deleteAllProduct();this.props.closeModal()}}
              >
                Thêm 
              </button>
            </div>
          </div>
        </div>
      </Modal>
    );
  }
}
