import React, {} from "react";
import "reactjs-popup/dist/index.css";
import Modal from "react-modal";
import * as myToast from "../../../Common/helper/toastHelper";
import * as customerApi from "../../apis/customer";

const customStyles = {
  content: {
    top: "40%",
    left: "50%",
    right: "auto",
    bottom: "auto",
    marginRight: "-50%",
    transform: "translate(-50%, -50%)",
  },
};

export default function DeleteModal(props) {
  function submitHandler() {
    customerApi
      .deleteCustomer(props.customer.makhachhang)
      .then((success) => {
        if (success.status) {
          myToast.toastSucces("Xoá khách hàng thành công");
          props.deleteFunction(props.customer);
          props.closeModal();
        }
      })
      .catch((error) => {
        myToast.toastError("Xoá khách hàng thất bại");
        console.error(error);
      });
  }

  return (
    <Modal
    isOpen={props.showModal}
    onRequestClose={props.closeModal}
    style={customStyles}
    contentLabel="Example Modal"
  >
    <div class="add-item-modal khachhangmodal" role="document">
      <div class="">
        <div class="modal-header">
          <h5 class="modal-title" id="exampleModalLongTitle">
            Bạn có muốn xoá khách hàng này
          </h5>
        </div>

        <div class="modal-footer">
          <button
            type="button"
            class="btn btn-secondary"
            data-dismiss="modal"  
            onClick={props.closeModal}
          >
            Đóng
          </button>
          <button
            type="button"
            class="btn btn-primary"
            onClick={submitHandler.bind(this)}
          >
            Xoá
          </button>
        </div>
      </div>
    </div>
  </Modal>
  )
}
