import React, { Component } from "react";
import AddModal from "./AddModal";
import "./style.css";
import * as productApi from "../../apis/product";
import ProductItem from "./ProductItem";
import _ from "lodash";

export default class index extends Component {
  state = {
    showModal: false,
    listmobile: [],
    listlaptop: [],
  };
  closeModal() {
    this.setState({ showModal: false });
  }
  openModal() {
    this.setState({ showModal: true });
  }

  async loadMobile() {
    productApi
      .allMobileProduct()
      .then((success) => {
        if (success.status === 200) {
          this.setState({ listmobile: success.data.value.$values });
        }
      })
      .catch((error) => {
        console.error(error);
      });
  }

  async loadLaptop() {
    productApi
      .allLaptopProduct()
      .then((success) => {
        if (success.status === 200) {
          this.setState({ listlaptop: success.data.value.$values });
        }
      })
      .catch((error) => {
        console.error(error);
      });
  }

  async componentDidMount() {
    this.loadMobile();
    this.loadLaptop();
  }


  async editProduct(product) {
    var listtemp = null;
    var stringlist = "";
    if (product.maloai === 1) {
      listtemp = this.state.listmobile;
      stringlist = "listmobile";
    } else {
      listtemp = this.state.listlaptop;
      stringlist = "listlaptop";
    }
    _.remove(listtemp, (n) => {
      return n.masanpham === product.masanpham;
    });

    listtemp.push(product);

    await this.setState({ [stringlist]: listtemp });
  }

  async addProduct(product) {
    var listtemp = null;
    var stringlist = "";
    if (product.maloai === 1) {
      listtemp = this.state.listmobile;
      stringlist = "listmobile";
    } else {
      listtemp = this.state.listlaptop;
      stringlist = "listlaptop";
    }
    listtemp.push(product);

    await this.setState({ [stringlist]: listtemp });
  }

  async deleteProduct(product) {
    var listtemp = null;
    var stringlist = "";
    if (product.maloai === 1) {
      listtemp = this.state.listmobile;
      stringlist = "listmobile";
    } else {
      listtemp = this.state.listlaptop;
      stringlist = "listlaptop";
    }

    _.remove(listtemp, (n) => {
      return n.masanpham === product.masanpham;
    });
   await this.setState({ [stringlist]: listtemp });
  }

